mapper.update:
UPDATE users
SET last_login_date = NOW()
WHERE user_id = #{userId}

mapper.select:
SELECT nickname
FROM users
WHERE nickname = #{nickname}

SELECT email
FROM users
WHERE email = #{email}

SELECT user_id as userId
FROM users
WHERE NAME = #{name}
AND email = #{email}

SELECT password
FROM users
WHERE user_id = #{userId}
AND email = #{email}

mapper.update:

UPDATE users
SET PASSWORD = #{password}
WHERE user_id = #{userId}
